={
  "sessionId": "test_session_12345",
  "documents": [
    {
      "id": "doc_001",
      "title": "AI Agents in Commercial Real Estate: A Survey",
      "source": "Journal of Real Estate Technology",
      "content": "This paper examines the application of AI agents in commercial real estate investment processes. Key findings indicate that AI agents can automate property valuation, market analysis, and investment decision-making.",
      "metadata": {
        "year": 2024,
        "authors": ["Smith, J.", "Johnson, A."],
        "type": "research_paper"
      }
    }
  ],
  "researchContext": "AI agents and agentic workflows in institutional commercial real estate investment",
  "agentType": "academic_opportunity",
  "systemPrompt": "You are an Academic Opportunity Identifier with expertise in research gap analysis and publication strategy. You excel at identifying underexplored areas, potential research collaborations, and publication opportunities in the intersection of AI agents and commercial real estate.\n\n## Role\nYou are an expert academic researcher and publication strategist specializing in AI and real estate technology.\n\n## Tools:\n- **Think Tool**: Use the **think** tool to think about something. It will not obtain new information or change the database, but just append the thought to the log.\n- **Firecrawl Tool**: Extract content from academic websites and publication databases\n- **Tavily Search Tool**: Search for academic papers, conferences, and research trends\n\n## Important:\nFirst step is to use the **think** tool to plan your steps. You should use your tools numerous times and iterate.\n\n## Core Capabilities\n- **Gap Analysis**: Identify underexplored research areas in AI agents and CRE\n- **Publication Mapping**: Find suitable journals, conferences, and publication venues\n- **Collaboration Identification**: Locate potential research partners and institutions\n- **Trend Analysis**: Analyze emerging research directions and opportunities\n\n## Task Strategy Guidelines\n1. Analyze existing research landscape\n2. Identify methodological gaps and limitations\n3. Map publication opportunities by venue and impact\n4. Suggest collaboration strategies and partnerships\n5. Prioritize opportunities by feasibility and impact\n\n## Example Workflow\n1. Think about analysis approach\n2. Search for recent publications in the field\n3. Extract content from key sources\n4. Analyze gaps and opportunities\n5. Generate structured recommendations\n\n## Final Reminders\nUse the **think** tool to plan your approach. Focus on actionable opportunities with clear publication pathways.",
  "taskPrompt": "Analyze the research landscape to identify academic and research opportunities:\n\n1. **Gap Analysis**: Identify underexplored research areas\n2. **Publication Opportunities**: Map potential publication venues and topics\n3. **Collaboration Recommendations**: Suggest research partnerships\n4. **Conference Targets**: Identify relevant conferences and symposiums\n5. **Funding Opportunities**: Highlight potential grant and funding sources\n\nFocus on creating actionable recommendations with specific next steps.",
  "outputSchema": {
    "type": "object",
    "properties": {
      "agent_type": {"type": "string"},
      "session_id": {"type": "string"},
      "analysis_summary": {"type": "string"},
      "research_gaps": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "gap_title": {"type": "string"},
            "description": {"type": "string"},
            "opportunity_level": {"type": "string", "enum": ["high", "medium", "low"]},
            "research_methods": {"type": "array", "items": {"type": "string"}},
            "potential_impact": {"type": "string"}
          },
          "required": ["gap_title", "description", "opportunity_level"]
        }
      },
      "publication_opportunities": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "venue_name": {"type": "string"},
            "venue_type": {"type": "string", "enum": ["journal", "conference", "workshop"]},
            "topic_fit": {"type": "string"},
            "submission_deadline": {"type": "string"},
            "impact_factor": {"type": "string"},
            "recommended_angle": {"type": "string"}
          },
          "required": ["venue_name", "venue_type", "topic_fit"]
        }
      },
      "collaboration_opportunities": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "institution": {"type": "string"},
            "researcher_name": {"type": "string"},
            "expertise_area": {"type": "string"},
            "collaboration_type": {"type": "string"},
            "contact_method": {"type": "string"}
          },
          "required": ["institution", "expertise_area", "collaboration_type"]
        }
      },
      "funding_opportunities": {
        "type": "array",
        "items": {
          "type": "object",
          "properties": {
            "funding_agency": {"type": "string"},
            "program_name": {"type": "string"},
            "amount_range": {"type": "string"},
            "deadline": {"type": "string"},
            "alignment_score": {"type": "string"}
          },
          "required": ["funding_agency", "program_name", "alignment_score"]
        }
      },
      "next_steps": {
        "type": "array",
        "items": {"type": "string"}
      }
    },
    "required": ["agent_type", "session_id", "analysis_summary", "research_gaps", "publication_opportunities", "next_steps"]
  },
  "tool1Description": "Extract content from academic websites, journal pages, and research databases to gather publication information and research details.",
  "tool1Url": "https://api.firecrawl.dev/v1/scrape",
  "tool1Body": "{\"url\": \"\" + $fromAI('url', 'The academic URL to analyze', 'string') + \"\", \"formats\": [\"markdown\"], \"onlyMainContent\": true}",
  "tool2Description": "Search for academic papers, research trends, conference information, and publication opportunities in AI and real estate domains.",
  "tool2Url": "https://api.tavily.com/search",
  "tool2Body": "{\"api_key\": \"\" + $credentials.tavilyApi.api_key + \"\", \"query\": \"\" + $fromAI('searchTerm', 'Academic search term for research opportunities', 'string') + \"\", \"search_depth\": \"advanced\", \"include_answer\": true, \"max_results\": 8, \"search_type\": \"academic\"}"
}
