parameters.jsonBody:
={
  "url": "{{ $fromAI('url', 'URL of conference or journal website to analyze', 'string') }}",
  "formats": [
    "markdown"
  ],
  "onlyMainContent": true
}

parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "sessionId": {"type": "string"},
    "agentType": {"type": "string"},
    "processingTimestamp": {"type": "string"},
    "researchGaps": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "gapCategory": {"type": "string"},
          "description": {"type": "string"},
          "significance": {"type": "string", "enum": ["high", "medium", "low"]},
          "feasibility": {"type": "string", "enum": ["high", "medium", "low"]},
          "timeToPublication": {"type": "string", "enum": ["6-12 months", "1-2 years", "2+ years"]},
          "potentialImpact": {"type": "string"}
        }
      }
    },
    "publicationOpportunities": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "venue": {"type": "string"},
          "type": {"type": "string", "enum": ["journal", "conference", "workshop"]},
          "impactFactor": {"type": "string"},
          "submissionDeadline": {"type": "string"},
          "focusAreas": {"type": "array", "items": {"type": "string"}},
          "recommendedTopics": {"type": "array", "items": {"type": "string"}},
          "collaborationPotential": {"type": "string", "enum": ["high", "medium", "low"]}
        }
      }
    },
    "collaborationOpportunities": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "institution": {"type": "string"},
          "researchers": {"type": "array", "items": {"type": "string"}},
          "expertise": {"type": "string"},
          "contactInfo": {"type": "string"},
          "fundingPotential": {"type": "string", "enum": ["high", "medium", "low"]},
          "geographicRegion": {"type": "string"}
        }
      }
    },
    "emergingTrends": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "trend": {"type": "string"},
          "description": {"type": "string"},
          "researchPotential": {"type": "string", "enum": ["high", "medium", "low"]},
          "timeHorizon": {"type": "string", "enum": ["6 months", "1 year", "2+ years"]},
          "relatedTechnologies": {"type": "array", "items": {"type": "string"}}
        }
      }
    },
    "fundingOpportunities": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "fundingSource": {"type": "string"},
          "programName": {"type": "string"},
          "maxAmount": {"type": "string"},
          "applicationDeadline": {"type": "string"},
          "eligibilityCriteria": {"type": "string"},
          "successProbability": {"type": "string", "enum": ["high", "medium", "low"]}
        }
      }
    },
    "strategicRecommendations": {
      "type": "array",
      "items": {
        "type": "object",
        "properties": {
          "recommendation": {"type": "string"},
          "rationale": {"type": "string"},
          "priority": {"type": "string", "enum": ["high", "medium", "low"]},
          "timeline": {"type": "string"},
          "resources": {"type": "string"}
        }
      }
    }
  },
  "required": ["sessionId", "agentType", "researchGaps", "publicationOpportunities", "emergingTrends", "strategicRecommendations"],
  "additionalProperties": false
}
